import { useState } from "react";
import { supabase } from "../supabaseClient";
import { toast } from "react-hot-toast";

export default function LeaveMessagePage() {
  const [text, setText] = useState("");
  const submit = async (e)=>{
    e.preventDefault();
    const { error } = await supabase.from("guest_messages").insert([{ text, approved: true, is_public: true }]);
    if (error) return toast.error(error.message);
    toast.success("Message submitted!"); setText("");
  };
  return (
    <div className="max-w-2xl mx-auto p-4">
      <div className="card p-6">
        <h2 className="section-title">Leave a message for Abraham & Jesse-Lee ðŸŒ»</h2>
        <form className="mt-4 space-y-3" onSubmit={submit}>
          <textarea className="input h-32" value={text} onChange={e=>setText(e.target.value)} placeholder="Share your wishes..." />
          <div className="flex justify-end"><button className="btn-primary" type="submit">Submit</button></div>
        </form>
      </div>
    </div>
  );
}
