import { Navigate } from "react-router-dom";
import { useAuth } from "../context/AuthProvider";
export default function RoleRoute({ roles = ["admin"], children, to = "/" }) {
  const { user, role, loading } = useAuth();
  if (loading) return <div className="min-h-[40vh] flex items-center justify-center"><div className="animate-pulse text-soil-600">Loadingâ€¦</div></div>;
  if (!user) return <Navigate to="/login" replace />;
  if (!roles.includes(role)) return <Navigate to={to} replace />;
  return children;
}
