// File: src/pages/UploadMediaPage.jsx
import GoogleDrivePicker from "../components/GoogleDrivePicker";

const BG_URL =
  "https://cyxeucwesqzwlisgveiz.supabase.co/storage/v1/object/public/public-assets/flowers-8309997.jpg";

// Clean up env values (remove quotes and anything after #)
const fromEnv = (v) => (v ? String(v).split("#")[0].replace(/^["']|["']$/g, "").trim() : "");

// Use your provided env var names
const PHOTOS_FOLDER = fromEnv(process.env.REACT_APP_DRIVE_PHOTOS_FOLDER_ID);
const VIDEOS_FOLDER = fromEnv(process.env.REACT_APP_DRIVE_VIDEOS_FOLDER_ID);

export default function UploadMediaPage() {
  return (
    <Backdrop>
      <Card className="space-y-6">
        <h1 className="text-3xl font-bold">Upload Wedding Media</h1>
        <p className="text-soil-700">
          Please upload your photos and videos. Larger videos may take a moment to process on Google Drive
          before previews work.
        </p>

        <div className="flex gap-3 flex-wrap">
          <GoogleDrivePicker
            targetFolderId={PHOTOS_FOLDER}
            buttonLabel="Upload Photos"
          />
          <GoogleDrivePicker
            targetFolderId={VIDEOS_FOLDER}
            buttonLabel="Upload Videos"
          />
        </div>

        {/* Optional hint if a folder id is missing */}
        {(!PHOTOS_FOLDER || !VIDEOS_FOLDER) && (
          <p className="text-sm text-red-600">
            One or more Drive folder IDs are missing. Check your .env and restart the dev server.
          </p>
        )}
      </Card>
    </Backdrop>
  );
}

/** UI wrappers (match other pages) */
function Backdrop({ children }) {
  return (
    <div
      className="min-h-screen w-full bg-center bg-cover relative"
      style={{ backgroundImage: `url(${BG_URL})` }}
    >
      <div className="absolute inset-0 bg-black/30 backdrop-blur-[1px]" />
      <div className="relative z-10 max-w-4xl mx-auto px-4 py-10">{children}</div>
    </div>
  );
}

function Card({ children, className = "" }) {
  return (
    <div
      className={`bg-white/85 rounded-2xl shadow-xl border border-white/60 p-6 md:p-8 ${className}`}
    >
      {children}
    </div>
  );
}
