import { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import { supabase } from "../supabaseClient";
import { toast } from "react-hot-toast";

export default function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  // If already logged in, decide where to go based on RSVP existence
  useEffect(() => {
    let mounted = true;
    (async () => {
      const { data } = await supabase.auth.getSession();
      const session = data?.session;
      if (!mounted || !session) return;
      await routeByRsvp(session.user.email, navigate);
    })();
    return () => { mounted = false; };
  }, [navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!email.trim() || !password) {
      toast.error("Please enter your email and password");
      return;
    }
    const { error } = await supabase.auth.signInWithPassword({
      email: email.trim(),
      password,
    });
    if (error) {
      toast.error(error.message);
      return;
    }
    toast.success("Logged in!");
    await routeByRsvp(email.trim(), navigate);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white shadow-lg rounded-lg p-8 w-full max-w-md">
        <h1 className="text-2xl font-bold text-center mb-6">Login</h1>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="input w-full"
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="input w-full"
          />
          <button type="submit" className="btn-primary w-full">
            Log In
          </button>
        </form>
        <div className="text-center mt-4">
          <Link to="/signup" className="text-sunflower-700 hover:underline">
            Create an account
          </Link>
          {" | "}
          <Link to="/reset" className="text-sunflower-700 hover:underline">
            Forgot password?
          </Link>
        </div>
      </div>
    </div>
  );
}

async function routeByRsvp(email, navigate) {
  try {
    const clean = (email || "").toLowerCase();
    if (!clean) return navigate("/rsvp", { replace: true });
    const { data, error } = await supabase
      .from("rsvps")
      .select("id")
      .eq("email", clean)
      .limit(1);
    if (error) throw error;
    if (data && data.length > 0) {
      navigate("/my-rsvp", { replace: true });
    } else {
      navigate("/rsvp", { replace: true });
    }
  } catch {
    // On any error, fall back to RSVP page
    navigate("/rsvp", { replace: true });
  }
}
