import useCountdown from "../hooks/useCountdown";
import { APP_VARS } from "../config/media";

export default function VenuePage() {
  const countdown = useCountdown(APP_VARS.WEDDING_DATE_TIME);
  return (
    <div className="max-w-5xl mx-auto p-4">
      <header className="my-6 text-center">
        <h1 className="text-3xl font-extrabold text-soil-700">Venue: {APP_VARS.VENUE_NAME || "To be announced"}</h1>
        {APP_VARS.WEDDING_DATE_TIME && (<p className="text-soil-600 mt-1">Countdown: <span className="badge">{countdown}</span></p>)}
      </header>
      <div className="card p-4">
        <p className="text-soil-600">{APP_VARS.VENUE_ADDRESS || "Address coming soon."}</p>
        <div className="mt-4 aspect-video rounded-xl2 overflow-hidden">
          <iframe title="Venue Map" className="w-full h-full" loading="lazy" allowFullScreen referrerPolicy="no-referrer-when-downgrade" src={`https://www.google.com/maps?q=${encodeURIComponent(APP_VARS.VENUE_ADDRESS || "South Africa")}&output=embed`} />
        </div>
        {APP_VARS.VENUE_ADDRESS && (
          <a className="btn-primary mt-4 inline-block" href={`https://www.google.com/maps/dir/?api=1&destination=${encodeURIComponent(APP_VARS.VENUE_ADDRESS)}`} target="_blank" rel="noreferrer">Get Directions</a>
        )}
      </div>
    </div>
  );
}
