// File: src/pages/WeddingAlbumPage.jsx
import { useEffect, useMemo, useState } from "react";
import { supabase } from "../supabaseClient";
import dayjs from "dayjs";

const PAGE_SIZE = 24;
const BG_URL =
  "https://cyxeucwesqzwlisgveiz.supabase.co/storage/v1/object/public/public-assets/flowers-8309997.jpg";

// small sanitizer to avoid stray quotes or inline comments leaking into URLs
const clean = (v) => (v ? String(v).split("#")[0].replace(/^["']|["']$/g, "").trim() : "");

function normalizeItem(it) {
  return {
    ...it,
    media_type: it.media_type || inferTypeFromMime(it.mime_type, it.preview_url),
    preview_url: clean(it.preview_url),
    uploader_name: clean(it.uploader_name) || "Guest",
    drive_name: it.drive_name || "File",
  };
}

function inferTypeFromMime(mime, url = "") {
  const m = (mime || "").toLowerCase();
  if (m.startsWith("image/")) return "image";
  if (m.startsWith("video/")) return "video";
  // fallback on URL hints
  if (/\.(png|jpe?g|gif|webp|bmp|heic)(\?|$)/i.test(url)) return "image";
  if (/\.(mp4|mov|webm|mkv|avi)(\?|$)/i.test(url)) return "video";
  return "file";
}

export default function WeddingAlbumPage() {
  const [items, setItems] = useState([]);
  const [page, setPage] = useState(0);
  const [hasMore, setHasMore] = useState(true);
  const [loading, setLoading] = useState(false);
  const [loadErr, setLoadErr] = useState("");

  const loadPage = async () => {
    if (loading || !hasMore) return;
    setLoading(true);
    setLoadErr("");
    const from = page * PAGE_SIZE;
    const to = from + PAGE_SIZE - 1;

    const { data, error } = await supabase
      .from("album_files")
      .select("*")
      .order("created_at", { ascending: false })
      .range(from, to);

    if (error) {
      console.error(error);
      setLoadErr(error.message || "Could not load album.");
    } else {
      const normalized = (data || []).map(normalizeItem);
      setItems((prev) => [...prev, ...normalized]);
      if (!data || data.length < PAGE_SIZE) setHasMore(false);
      setPage((p) => p + 1);
    }
    setLoading(false);
  };

  useEffect(() => {
    loadPage();
    // eslint-disable-next-line
  }, []);

  // Group by yyyy-mm-dd
  const groups = useMemo(() => {
    const map = {};
    for (const it of items) {
      const key = dayjs(it.created_at).format("YYYY-MM-DD");
      if (!map[key]) map[key] = [];
      map[key].push(it);
    }
    return Object.entries(map).sort(([a], [b]) => (a < b ? 1 : -1));
  }, [items]);

  // Infinite scroll
  useEffect(() => {
    const onScroll = () => {
      const nearBottom =
        window.innerHeight + window.scrollY >= document.body.offsetHeight - 300;
      if (nearBottom) loadPage();
    };
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
    // eslint-disable-next-line
  }, [page, hasMore, loading]);

  return (
    <Backdrop>
      <Card>
        <header className="mb-6">
          <h1 className="section-title">Wedding Album</h1>
          <p className="text-soil-700">
            Photos and videos shared by guests and the couple. New uploads may take a moment
            to process on Google Drive before previews are available.
          </p>
        </header>

        {loadErr && (
          <div className="text-sm text-red-600 bg-red-50 border border-red-200 rounded-md p-2 mb-4">
            {loadErr}
          </div>
        )}

        {items.length === 0 && !loading && !loadErr && (
          <p className="text-soil-700">No media yet. Be the first to upload!</p>
        )}

        <div className="space-y-8">
          {groups.map(([date, arr]) => (
            <section key={date}>
              <h2 className="text-lg font-semibold text-soil-800 mb-3">
                {dayjs(date).format("DD MMM YYYY")}
              </h2>
              <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-3">
                {arr.map((it) => (
                  <AlbumCard key={it.id} item={it} />
                ))}
              </div>
            </section>
          ))}
        </div>

        {loading && (
          <p className="mt-6 text-soil-600">Loadingâ€¦</p>
        )}
        {!hasMore && items.length > 0 && (
          <p className="mt-6 text-soil-600">End of album.</p>
        )}
      </Card>
    </Backdrop>
  );
}

function AlbumCard({ item }) {
  const { media_type, preview_url, drive_name, uploader_name, created_at } = item;

  const title = `${drive_name} â€” Uploaded by ${uploader_name}`;
  const time = dayjs(created_at).format("HH:mm");

  return (
    <a
      href={preview_url}
      target="_blank"
      rel="noreferrer"
      className="block group rounded-xl overflow-hidden bg-white/70 border border-white/60 hover:bg-white/80 transition"
      title={title}
    >
      {media_type === "image" ? (
        <img
          src={preview_url}
          alt={drive_name}
          className="w-full h-40 object-cover group-hover:opacity-90"
          loading="lazy"
        />
      ) : media_type === "video" ? (
        <div className="w-full h-40 flex items-center justify-center text-sm text-soil-700">
          Open video â†—
        </div>
      ) : (
        <div className="w-full h-40 flex items-center justify-center text-sm text-soil-600">
          Open file â†—
        </div>
      )}
      <div className="p-2 text-xs text-soil-700 flex justify-between">
        <span className="truncate">{uploader_name}</span>
        <span>{time}</span>
      </div>
    </a>
  );
}

/** UI wrappers to match the rest of the site */
function Backdrop({ children }) {
  return (
    <div
      className="min-h-screen w-full bg-center bg-cover relative"
      style={{ backgroundImage: `url(${BG_URL})` }}
    >
      <div className="absolute inset-0 bg-black/30 backdrop-blur-[1px]" />
      <div className="relative z-10 max-w-6xl mx-auto px-4 py-10">{children}</div>
    </div>
  );
}

function Card({ children, className = "" }) {
  return (
    <div
      className={`bg-white/85 rounded-2xl shadow-xl border border-white/60 p-6 md:p-8 ${className}`}
    >
      {children}
    </div>
  );
}
