// File: src/components/GoogleDrivePicker.jsx
import { useCallback, useMemo, useRef, useState } from "react";

// ENV (CRA)
const API_KEY = process.env.REACT_APP_GOOGLE_API_KEY;          // required
const CLIENT_ID = process.env.REACT_APP_GOOGLE_CLIENT_ID;      // required
// Scopes: upload to user's Drive (and to a provided folder)
const SCOPES = "https://www.googleapis.com/auth/drive.file";

function loadScript(src) {
  return new Promise((resolve, reject) => {
    if (document.querySelector(`script[src="${src}"]`)) return resolve();
    const s = document.createElement("script");
    s.src = src;
    s.async = true;
    s.onload = () => resolve();
    s.onerror = () => reject(new Error(`Failed to load ${src}`));
    document.head.appendChild(s);
  });
}

async function ensureGoogleApis() {
  // 1) GIS OAuth
  await loadScript("https://accounts.google.com/gsi/client");
  // 2) GAPI (picker + client)
  await loadScript("https://apis.google.com/js/api.js");
  // Ensure gapi object is loaded
  await new Promise((resolve) => {
    if (window.gapi?.load) return resolve();
    const check = setInterval(() => {
      if (window.gapi?.load) {
        clearInterval(check);
        resolve();
      }
    }, 20);
  });
  // Load picker + client modules
  await new Promise((resolve) => window.gapi.load("client:picker", resolve));
}

export default function GoogleDrivePicker({
  targetFolderId,
  buttonLabel = "Upload to Drive",
  onPicked, // optional callback(files)
}) {
  const [busy, setBusy] = useState(false);
  const tokenClientRef = useRef(null);

  const ready = useMemo(() => Boolean(API_KEY && CLIENT_ID), []);

  const getAccessToken = useCallback(() => {
    return new Promise(async (resolve, reject) => {
      try {
        await ensureGoogleApis();

        if (!tokenClientRef.current) {
          tokenClientRef.current = window.google.accounts.oauth2.initTokenClient({
            client_id: CLIENT_ID,
            scope: SCOPES,
            callback: (resp) => {
              if (resp.error) reject(resp);
              else resolve(resp.access_token);
            },
          });
        } else {
          // set a fresh callback for this request
          tokenClientRef.current.callback = (resp) => {
            if (resp.error) reject(resp);
            else resolve(resp.access_token);
          };
        }
        tokenClientRef.current.requestAccessToken({ prompt: "" });
      } catch (e) {
        reject(e);
      }
    });
  }, []);

  const openPicker = useCallback(async () => {
    try {
      setBusy(true);
      const accessToken = await getAccessToken();

      // Init gapi client with our API key (Picker needs developerKey)
      await window.gapi.client.init({ apiKey: API_KEY });

      // Build upload view; if folder provided, upload directly into it
      const uploadView = new window.google.picker.DocsUploadView();
      if (targetFolderId) {
        uploadView.setParent(targetFolderId);
      }

      const picker = new window.google.picker.PickerBuilder()
        .addView(uploadView)
        .enableFeature(window.google.picker.Feature.MULTISELECT_ENABLED)
        .setOAuthToken(accessToken)
        .setDeveloperKey(API_KEY)
        .setOrigin(window.location.protocol + "//" + window.location.host)
        .setCallback((data) => {
          if (data.action === window.google.picker.Action.PICKED) {
            const files = (data.docs || []).map((d) => ({
              id: d.id,
              name: d.name,
              type: d.type,
              url: d.url,
              mimeType: d.mimeType,
            }));
            onPicked?.(files);
          }
        })
        .build();

      picker.setVisible(true);
    } catch (err) {
      console.error(err);
      alert(
        err?.message ||
          "Could not open Google Drive Picker. Check API key/client ID and allowed origins."
      );
    } finally {
      setBusy(false);
    }
  }, [getAccessToken, onPicked, targetFolderId]);

  return (
    <button
      type="button"
      className="btn-primary"
      disabled={!ready || busy}
      onClick={openPicker}
      title={!ready ? "Missing Google API env vars" : ""}
    >
      {busy ? "Opening…" : buttonLabel}
    </button>
  );
}
