import { useEffect, useState } from "react";
import { supabase } from "../supabaseClient";

export default function MessageWallPage() {
  const [messages, setMessages] = useState([]); const [expanded, setExpanded] = useState(null);
  useEffect(() => { (async ()=>{
      const { data, error } = await supabase.from("guest_messages").select("*").eq("approved", true).eq("is_public", true).order("created_at",{ascending:false});
      if (!error && data) setMessages(data);
    })();
  }, []);
  return (
    <div className="max-w-4xl mx-auto p-4">
      <header className="my-6 text-center">
        <h1 className="text-3xl font-extrabold text-soil-700">Message Wall ðŸŒ»</h1>
        <p className="text-soil-500">Warm wishes from friends & family</p>
      </header>
      <div className="space-y-3">
        {messages.map((m)=>(
          <div key={m.id} className="card p-4">
            <p className={`text-soil-700 ${expanded===m.id ? "" : "line-clamp-3"}`}>{m.text}</p>
            <div className="flex justify-between items-center mt-3">
              <span className="text-xs text-soil-500">{new Date(m.created_at).toLocaleString()}</span>
              <button className="btn-outline" onClick={()=>setExpanded(expanded===m.id?null:m.id)}>{expanded===m.id?"Collapse":"Expand"}</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
