import { useState } from "react";
import { useAuth } from "../context/AuthProvider";
import { toast } from "react-hot-toast";
import { Link, useLocation, useNavigate } from "react-router-dom";

export default function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPw, setShowPw] = useState(false);
  const [loading, setLoading] = useState(false);
  const [formErr, setFormErr] = useState("");
  const { signIn } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();
  const redirectTo = (location.state && location.state.from) || "/";

  const submit = async (e) => {
    e.preventDefault();
    setFormErr("");

    if (!email || !password) {
      setFormErr("Please enter both email and password.");
      return;
    }
    setLoading(true);
    try {
      await signIn({ email: email.trim(), password });
      toast.success("Logged in!");
      navigate(redirectTo, { replace: true });
    } catch (err) {
      const msg = err?.message || "Login failed. Please try again.";
      setFormErr(msg);
      toast.error(msg);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-[70vh] flex items-center justify-center p-4">
      <form onSubmit={submit} className="card p-6 w-full max-w-md">
        <h1 className="section-title text-center">Welcome ðŸŒ»</h1>

        {!!formErr && (
          <div className="mt-3 text-sm text-red-600 bg-red-50 border border-red-200 rounded-md p-2">
            {formErr}
          </div>
        )}

        <div className="mt-4 space-y-3">
          <label className="block">
            <span className="sr-only">Email</span>
            <input
              className="input"
              type="email"
              placeholder="Email"
              autoComplete="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              disabled={loading}
              required
            />
          </label>

          <label className="block relative">
            <span className="sr-only">Password</span>
            <input
              className="input pr-12"
              type={showPw ? "text" : "password"}
              placeholder="Password"
              autoComplete="current-password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              disabled={loading}
              required
            />
            <button
              type="button"
              className="absolute right-2 top-1/2 -translate-y-1/2 text-sm text-soil-600 hover:underline"
              onClick={() => setShowPw((s) => !s)}
              tabIndex={-1}
            >
              {showPw ? "Hide" : "Show"}
            </button>
          </label>

          <button className="btn-primary w-full" type="submit" disabled={loading}>
            {loading ? "Signing inâ€¦" : "Login"}
          </button>

          <div className="flex items-center justify-between text-sm mt-2">
            <Link to="/signup" className="text-soil-600 hover:underline">
              Create account
            </Link>
            <Link to="/reset" className="text-soil-600 hover:underline">
              Forgot password?
            </Link>
          </div>
        </div>
      </form>
    </div>
  );
}
