import { useState } from "react";
import { useAuth } from "../context/AuthProvider";
import { toast } from "react-hot-toast";

export default function ResetPasswordPage() {
  const [email,setEmail]=useState(""); const { resetPassword } = useAuth();

  const submit = async (e)=>{
    e.preventDefault();
    try {
      const redirectTo = `${window.location.origin}/login`;
      await resetPassword({ email, redirectTo });
      toast.success("Password reset link sent!");
    } catch(err){ toast.error(err.message); }
  };

  return (
    <div className="min-h-[70vh] flex items-center justify-center p-4">
      <form onSubmit={submit} className="card p-6 w-full max-w-md">
        <h1 className="section-title text-center">Reset Password ðŸŒ»</h1>
        <div className="mt-4 space-y-3">
          <input className="input" type="email" placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} />
          <button className="btn-primary w-full" type="submit">Send Reset Link</button>
        </div>
      </form>
    </div>
  );
}
