// src/pages/ViewRSVPPage.jsx
import { useEffect, useMemo, useState } from "react";
import { supabase } from "../supabaseClient";
import Backdrop from "../components/Backdrop";
import Card from "../components/Card";
import confetti from "canvas-confetti";
import dayjs from "dayjs";
import duration from "dayjs/plugin/duration";
import { ENV } from "../utils/fromEnv";
import { safeUpdatePayload } from "../utils/safeUpdate";
import { notifyError, notifySuccess } from "../context/ToastContext";

dayjs.extend(duration);

export default function ViewRSVPPage() {
  const [rsvp, setRsvp] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [loading, setLoading] = useState(true);
  const [countdown, setCountdown] = useState("");

  const weddingDateTime = useMemo(() => {
    // Fallback to 2026â€‘02â€‘21T14:30:00+02:00 if env missing
    const iso = ENV.WEDDING_DATE_TIME || "2026-02-21T14:30:00+02:00";
    return dayjs(iso);
  }, []);

  useEffect(() => {
    let t = null;
    const tick = () => {
      const diff = weddingDateTime.diff(dayjs());
      if (diff <= 0) {
        setCountdown("It's the big day! ðŸŽ‰");
        return;
      }
      const d = dayjs.duration(diff);
      setCountdown(
        `${d.days()}d ${d.hours()}h ${d.minutes()}m ${d.seconds()}s`
      );
      t = setTimeout(tick, 1000);
    };
    tick();
    return () => t && clearTimeout(t);
  }, [weddingDateTime]);

  useEffect(() => {
    (async () => {
      setLoading(true);
      const { data: session } = await supabase.auth.getSession();
      const email = session?.session?.user?.email;
      if (!email) {
        setLoading(false);
        return;
      }
      const { data, error } = await supabase
        .from("rsvps")
        .select("*")
        .eq("email", email)
        .order("created_at", { ascending: false })
        .limit(1)
        .single();

      if (error && error.code !== "PGRST116") {
        notifyError("Failed to load your RSVP.");
      }
      setRsvp(data || null);
      setLoading(false);

      if (data) {
        // celebration on load if already RSVPed
        setTimeout(() => {
          confetti({ particleCount: 120, spread: 75, origin: { y: 0.6 } });
        }, 300);
      }
    })();
  }, []);

  const handleSave = async () => {
    if (!rsvp) return;
    const payload = safeUpdatePayload(rsvp);
    const { error } = await supabase
      .from("rsvps")
      .update(payload)
      .eq("id", rsvp.id);

    if (error) {
      notifyError("Update failed.");
      return;
    }
    notifySuccess("RSVP updated.");
    setEditMode(false);
  };

  return (
    <Backdrop>
      <div className="container mx-auto px-4 py-10">
        <Card className="mx-auto max-w-3xl p-4 sm:p-6 text-white">
          <div className="flex flex-col gap-4 sm:gap-6">
            <header className="text-center">
              <h1 className="text-2xl sm:text-3xl font-semibold tracking-tight">
                View / Edit RSVP
              </h1>
              <p className="mt-2 text-sm sm:text-base text-white/80">
                Countdown: <span className="font-medium">{countdown}</span>
              </p>
            </header>

            {loading ? (
              <p role="status" className="text-center">Loadingâ€¦</p>
            ) : !rsvp ? (
              <div className="text-center">
                <p className="mb-4">We couldn't find your RSVP.</p>
                <a
                  href="/"
                  className="btn-primary inline-block"
                  aria-label="Go to RSVP page"
                >
                  Go to RSVP
                </a>
              </div>
            ) : (
              <>
                {/* Thank-you card view (when not editing) */}
                {!editMode && (
                  <div
                    className="rounded-xl border border-white/10 bg-white/10 p-4"
                    role="status"
                    aria-live="polite"
                  >
                    <p className="text-lg">
                      Thank you for RSVPing,{" "}
                      <span className="font-semibold break-words">
                        {rsvp.full_name || "Guest"}
                      </span>
                      !
                    </p>
                    <p className="mt-2 text-white/80">
                      Status:{" "}
                      {rsvp.attending ? (
                        <span className="badge bg-green-600/80">Attending</span>
                      ) : (
                        <span className="badge bg-gray-600/80">Not attending</span>
                      )}
                    </p>
                    {rsvp.dietary ? (
                      <p className="mt-2 break-words">
                        Dietary: <span className="font-medium">{rsvp.dietary}</span>
                      </p>
                    ) : null}
                    {rsvp.songs ? (
                      <p className="mt-2 break-words">
                        Songs: <span className="font-medium">{rsvp.songs}</span>
                      </p>
                    ) : null}

                    <div className="mt-4 flex flex-col sm:flex-row gap-3">
                      <button
                        type="button"
                        onClick={() => setEditMode(true)}
                        className="btn-primary w-full sm:w-auto"
                      >
                        Edit RSVP
                      </button>
                      <a href="/upload-media" className="btn-outline w-full sm:w-auto">
                        Upload Wedding Media
                      </a>
                      <a href="/wedding-album" className="btn-outline w-full sm:w-auto">
                        View Album
                      </a>
                    </div>
                  </div>
                )}

                {/* Edit form */}
                {editMode && (
                  <form
                    className="grid gap-4"
                    onSubmit={(e) => {
                      e.preventDefault();
                      handleSave();
                    }}
                  >
                    <div className="grid sm:grid-cols-2 gap-4">
                      <div>
                        <label className="block text-sm mb-1" htmlFor="full_name">
                          Full name
                        </label>
                        <input
                          id="full_name"
                          className="input w-full"
                          value={rsvp.full_name || ""}
                          onChange={(e) =>
                            setRsvp((s) => ({ ...s, full_name: e.target.value }))
                          }
                          placeholder="Full name"
                        />
                      </div>
                      <div>
                        <label className="block text-sm mb-1" htmlFor="email">
                          Email
                        </label>
                        <input
                          id="email"
                          className="input w-full"
                          value={rsvp.email || ""}
                          disabled
                          aria-disabled="true"
                        />
                      </div>
                    </div>

                    <div className="grid sm:grid-cols-2 gap-4">
                      <div>
                        <label className="block text-sm mb-1">Attending?</label>
                        <div className="flex items-center gap-4">
                          <label className="inline-flex items-center gap-2">
                            <input
                              type="radio"
                              name="attending"
                              checked={!!rsvp.attending}
                              onChange={() =>
                                setRsvp((s) => ({ ...s, attending: true }))
                              }
                            />
                            <span>Yes</span>
                          </label>
                          <label className="inline-flex items-center gap-2">
                            <input
                              type="radio"
                              name="attending"
                              checked={!rsvp.attending}
                              onChange={() =>
                                setRsvp((s) => ({ ...s, attending: false }))
                              }
                            />
                            <span>No</span>
                          </label>
                        </div>
                      </div>
                      <div>
                        <label className="block text-sm mb-1" htmlFor="dietary">
                          Dietary requirements
                        </label>
                        <input
                          id="dietary"
                          className="input w-full"
                          value={rsvp.dietary || ""}
                          onChange={(e) =>
                            setRsvp((s) => ({ ...s, dietary: e.target.value }))
                          }
                          placeholder="Vegetarian, halal, allergiesâ€¦"
                        />
                      </div>
                    </div>

                    <div>
                      <label className="block text-sm mb-1" htmlFor="songs">
                        Song requests
                      </label>
                      <textarea
                        id="songs"
                        className="input w-full min-h-[96px]"
                        value={rsvp.songs || ""}
                        onChange={(e) =>
                          setRsvp((s) => ({ ...s, songs: e.target.value }))
                        }
                        placeholder="Optional"
                      />
                    </div>

                    <div className="flex flex-col sm:flex-row gap-3 justify-end">
                      <button
                        type="button"
                        onClick={() => setEditMode(false)}
                        className="btn-outline w-full sm:w-auto"
                      >
                        Cancel
                      </button>
                      <button type="submit" className="btn-primary w-full sm:w-auto">
                        Save Changes
                      </button>
                    </div>
                  </form>
                )}
              </>
            )}
          </div>
        </Card>
      </div>
    </Backdrop>
  );
}
