import { useState } from "react";
import { useAuth } from "../context/AuthProvider";
import { toast } from "react-hot-toast";
import { Link, useNavigate } from "react-router-dom";

export default function LoginPage() {
  const [email,setEmail]=useState(""); const [password,setPassword]=useState("");
  const { signIn } = useAuth(); const navigate = useNavigate();

  const submit = async (e)=>{
    e.preventDefault();
    try { await signIn({ email, password }); toast.success("Logged in!"); navigate("/"); }
    catch(err){ toast.error(err.message); }
  };

  return (
    <div className="min-h-[70vh] flex items-center justify-center p-4">
      <form onSubmit={submit} className="card p-6 w-full max-w-md">
        <h1 className="section-title text-center">Welcome ðŸŒ»</h1>
        <div className="mt-4 space-y-3">
          <input className="input" type="email" placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} />
          <input className="input" type="password" placeholder="Password" value={password} onChange={e=>setPassword(e.target.value)} />
          <button className="btn-primary w-full" type="submit">Login</button>
          <div className="flex items-center justify-between text-sm mt-2">
            <Link to="/signup" className="text-soil-600 hover:underline">Create account</Link>
            <Link to="/reset" className="text-soil-600 hover:underline">Forgot password?</Link>
          </div>
        </div>
      </form>
    </div>
  );
}
