// File: src/pages/ViewRSVPPage.jsx
import { useEffect, useMemo, useRef, useState } from "react";
import { useNavigate } from "react-router-dom";
import { supabase } from "../supabaseClient";
import { toast } from "react-hot-toast";
import confetti from "canvas-confetti";
import dayjs from "dayjs";
import duration from "dayjs/plugin/duration";
dayjs.extend(duration);

const BG_URL =
  "https://cyxeucwesqzwlisgveiz.supabase.co/storage/v1/object/public/public-assets/flowers-8309997.jpg";

// ---- Wedding config via .env (CRA requires REACT_APP_*) ----
const fromEnv = (v, fallback = "") =>
  (v ? String(v).split("#")[0].trim() : fallback);

const WEDDING_AT = dayjs(
  fromEnv(process.env.REACT_APP_WEDDING_DATE_TIME, "2025-11-08T14:30:00+02:00")
);

const VENUE_NAME = fromEnv(process.env.REACT_APP_VENUE_NAME, "Wedding Venue");
const VENUE_ADDRESS = fromEnv(
  process.env.REACT_APP_VENUE_ADDRESS,
  "Venue address to be announced"
);


// Columns that must NEVER be sent in an .update()
const BLOCKED_UPDATE_FIELDS = new Set([
  "id",
  "created_at",
  "user_id",
  "submitted_by",
  "email",
]);

export default function ViewRSVPPage() {
  const navigate = useNavigate();

  const [rsvp, setRsvp] = useState(null);
  const [loading, setLoading] = useState(true);
  const [edit, setEdit] = useState(false);
  const [form, setForm] = useState(null);
  const [formErr, setFormErr] = useState("");
  const [countdown, setCountdown] = useState(getCountdownText(WEDDING_AT));
  const confettiFired = useRef(false);
  const [authEmail, setAuthEmail] = useState(null);

  const isValidEmail = useMemo(
    () => (v) => /^\S+@\S+\.\S+$/.test(v || ""),
    []
  );

  useEffect(() => {
    (async () => {
      try {
        const { data: auth } = await supabase.auth.getUser();
        const email = auth?.user?.email || null;
        setAuthEmail(email);
        if (!email) {
          setLoading(false);
          return;
        }

        const { data: rows, error } = await supabase
          .from("rsvps")
          .select("*")
          .eq("email", email.toLowerCase())
          .order("created_at", { ascending: false })
          .limit(1);

        if (error) throw error;

        if (rows && rows.length) {
          const norm = normalizeRsvp(rows[0]);
          setRsvp(norm);
          setForm(norm);
          setEdit(false);
        } else {
          // No RSVP yet â€” prepare a clean form for CREATE
          const fresh = normalizeRsvp({
            full_name: "",
            email: email.toLowerCase(),
            attending: true,
            dietary: "",
            songs: "",
            has_plus_one: false,
            plus_one_name: "",
            plus_one_dietary: "",
            has_children: false,
            children: [],
          });
          setForm(fresh);
          setEdit(true); // open in create/edit mode
        }
      } catch (e) {
        toast.error(e?.message || "Could not load your RSVP.");
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  // Countdown tick
  useEffect(() => {
    const id = setInterval(() => {
      setCountdown(getCountdownText(WEDDING_AT));
    }, 1000);
    return () => clearInterval(id);
  }, []);

  // Confetti on thank-you view (once)
  useEffect(() => {
    if (!loading && rsvp && !edit && !confettiFired.current) {
      confettiFired.current = true;
      confetti({ particleCount: 120, spread: 70, origin: { y: 0.6 } });
      setTimeout(
        () => confetti({ particleCount: 80, spread: 80, origin: { y: 0.6 } }),
        400
      );
    }
  }, [loading, rsvp, edit]);

  const setField = (key, val) => setForm((f) => ({ ...f, [key]: val }));

  const togglePlusOne = (checked) => {
    setForm((f) => ({
      ...f,
      has_plus_one: checked,
      ...(checked ? {} : { plus_one_name: "", plus_one_dietary: "" }),
    }));
  };

  const toggleChildren = (checked) => {
    setForm((f) => ({
      ...f,
      has_children: checked,
      ...(checked ? {} : { children: [] }),
    }));
  };

  const addChild = () =>
    setForm((f) => ({
      ...f,
      children: [...(f.children || []), { name: "", dietary: "" }],
    }));

  const updateChild = (i, key, val) =>
    setForm((f) => {
      const copy = [...(f.children || [])];
      copy[i] = { ...copy[i], [key]: val };
      return { ...f, children: copy };
    });

  const removeChild = (i) =>
    setForm((f) => {
      const copy = [...(f.children || [])];
      copy.splice(i, 1);
      return { ...f, children: copy };
    });

  /** Build a clean payload for insert/update */
  const buildNormalizedPayload = (src, { forUpdate }) => {
    const cleaned = {
      // Always normalize these fields from the form state
      full_name: (src.full_name || "").trim(),
      attending: !!src.attending,
      dietary: src.dietary || "",
      songs: src.songs || "",
      has_plus_one: !!src.has_plus_one,
      plus_one_name: src.has_plus_one ? src.plus_one_name || "" : "",
      plus_one_dietary: src.has_plus_one ? src.plus_one_dietary || "" : "",
      has_children: !!src.has_children,
      children: Array.isArray(src.children) ? src.children : [],
    };

    if (!cleaned.has_children) cleaned.children = [];

    // For INSERT, include email; for UPDATE, explicitly exclude it
    if (!forUpdate) {
      cleaned.email = (src.email || authEmail || "").trim().toLowerCase();
    }

    // For UPDATE, ensure we exclude any blocked fields (defense in depth)
    if (forUpdate) {
      for (const k of Object.keys(src)) {
        if (BLOCKED_UPDATE_FIELDS.has(k)) continue;
        if (!(k in cleaned)) {
          cleaned[k] = src[k];
        }
      }
    }

    return cleaned;
  };

  const save = async () => {
    try {
      setFormErr("");

      // Basic validation
      if (!form?.full_name?.trim())
        return setFormErr("Please enter your full name.");

      const emailForInsert = (form?.email || authEmail || "").trim().toLowerCase();
      if (!rsvp && !isValidEmail(emailForInsert)) {
        return setFormErr("Your email address looks invalid.");
      }

      if (rsvp?.id) {
        // UPDATE â€” exclude blocked fields
        const payload = buildNormalizedPayload(form, { forUpdate: true });

        const { error } = await supabase
          .from("rsvps")
          .update(payload) // payload has NO id, created_at, user_id, submitted_by, email
          .eq("id", rsvp.id);

        if (error) throw error;

        toast.success("RSVP updated!");
        // Merge back into local state (preserving immutable fields)
        setRsvp((prev) => normalizeRsvp({ ...prev, ...payload }));
        setEdit(false);
      } else {
        // INSERT â€” include email
        const payload = buildNormalizedPayload(
          { ...form, email: emailForInsert },
          { forUpdate: false }
        );

        const { data, error } = await supabase
          .from("rsvps")
          .insert(payload)
          .select("*")
          .limit(1)
          .single();

        if (error) throw error;

        const norm = normalizeRsvp(data);
        toast.success("RSVP submitted!");
        setRsvp(norm);
        setForm(norm);
        setEdit(false);
        // Confetti will fire via the useEffect for the thank-you view
        confettiFired.current = false; // allow the thank-you confetti to run
      }
    } catch (err) {
      toast.error(err?.message || "Could not save RSVP.");
    }
  };

  // Loading
  if (loading) {
    return (
      <div className="min-h-[60vh] flex items-center justify-center">
        <div className="text-soil-600">Loading your RSVPâ€¦</div>
      </div>
    );
  }

  // Not logged in
  if (!authEmail) {
    return (
      <Backdrop>
        <Card>
          <h2 className="section-title">Please sign in</h2>
          <p className="text-soil-700 mt-2">
            You need to be logged in to view or submit your RSVP.
          </p>
        </Card>
      </Backdrop>
    );
  }

  // Thank-you / view mode
  if (rsvp && !edit) {
    return (
      <Backdrop>
        <Card>
          <h2 className="section-title">Thank you for RSVPing! ðŸŒ»</h2>
          <p className="text-soil-700 mt-1">
            Weâ€™re excited to celebrate Abraham &amp; Jesseâ€‘Lee with you.
          </p>

          {/* Wedding details (from .env) */}
          <div className="mt-4 p-4 rounded-xl bg-white/70 border border-white/60">
            <h3 className="font-semibold text-soil-800 mb-1">Wedding Details</h3>
            <p className="text-soil-700">
              <span className="font-medium">Date &amp; Time:</span>{" "}
              {WEDDING_AT.format("dddd, D MMMM YYYY [at] HH:mm")} (GMT+2)
            </p>
            <p className="text-soil-700">
              <span className="font-medium">Venue:</span> {VENUE_NAME}
            </p>
            <p className="text-soil-700">
              <span className="font-medium">Address:</span> {VENUE_ADDRESS}
            </p>
          </div>

          {/* RSVP quick facts */}
          <div className="mt-4 flex flex-wrap items-center gap-3">
            <div className="badge">
              Status: {rsvp.attending ? "Attending" : "Not Attending"}
            </div>
            {rsvp.dietary && <div className="badge">Dietary: {rsvp.dietary}</div>}
            {rsvp.songs && <div className="badge">Songs: {rsvp.songs}</div>}
            {rsvp.has_plus_one && rsvp.plus_one_name && (
              <div className="badge">Plus One: {rsvp.plus_one_name}</div>
            )}
            {rsvp.has_children && Array.isArray(rsvp.children) && rsvp.children.length > 0 && (
              <div className="badge">
                Children: {rsvp.children.map((c) => c.name).filter(Boolean).join(", ")}
              </div>
            )}
          </div>

          {/* Countdown */}
          <div className="mt-6 p-4 rounded-xl bg-white/70 border border-white/60">
            <h3 className="font-semibold text-soil-800 mb-1">Countdown to the big day</h3>
            <p className="text-soil-700">{countdown}</p>
          </div>

          {/* Money over gifts â€” loving note */}
          <div className="mt-6 p-4 rounded-xl bg-white/70 border border-white/60">
            <p className="text-soil-700">
              Your presence is the greatest gift. If youâ€™d like to bless us further, weâ€™re humbly
              choosing <span className="font-semibold">monetary gifts</span> over physical presents
              as we build our first home together. Thank you for loving us so generously. ðŸ’›
            </p>
          </div>

          {/* Actions: Album & Venue */}
          <div className="mt-6 grid md:grid-cols-2 gap-4">
            <div className="p-4 rounded-xl bg-white/70 border border-white/60">
              <h4 className="font-semibold text-soil-800">Wedding Album</h4>
              <p className="text-sm text-soil-700 mt-1">
                Browse and download photos/videos shared by guests and the couple.
              </p>
              <button
                className="btn-primary mt-3"
                onClick={() => navigate("/wedding-album")}
              >
                Open Wedding Album
              </button>
            </div>

            <div className="p-4 rounded-xl bg-white/70 border border-white/60">
              <h4 className="font-semibold text-soil-800">Venue Info</h4>
              <p className="text-sm text-soil-700 mt-1">
                Get directions, dress code, parking details, and the live weather.
              </p>
              <button className="btn-primary mt-3" onClick={() => navigate("/venue")}>
                View Venue Information
              </button>
            </div>
          </div>

          <button className="btn-primary mt-6" onClick={() => setEdit(true)}>
            Edit My RSVP
          </button>
        </Card>
      </Backdrop>
    );
  }

  // Create/Edit form (shared)
  const isCreate = !rsvp;
  return (
    <Backdrop>
      <Card className="space-y-4">
        <h2 className="section-title">{isCreate ? "Submit My RSVP" : "Edit My RSVP"}</h2>

        {!!formErr && (
          <div className="text-sm text-red-600 bg-red-50 border border-red-200 rounded-md p-2">
            {formErr}
          </div>
        )}

        <div className="grid md:grid-cols-2 gap-4">
          <input
            className="input"
            placeholder="Full Name"
            value={form?.full_name || ""}
            onChange={(e) => setField("full_name", e.target.value)}
            required
          />
          <input
            className="input opacity-80"
            type="email"
            placeholder="Email"
            value={(isCreate ? form?.email : authEmail) || ""}
            disabled
            title="Email is locked to keep your RSVP linked to your account."
          />
        </div>

        <div className="grid md:grid-cols-2 gap-4">
          <select
            className="input"
            value={String(!!form?.attending)}
            onChange={(e) => setField("attending", e.target.value === "true")}
          >
            <option value="true">Attending</option>
            <option value="false">Not Attending</option>
          </select>
          <input
            className="input"
            placeholder="Dietary Requirements"
            value={form?.dietary || ""}
            onChange={(e) => setField("dietary", e.target.value)}
          />
        </div>

        <input
          className="input"
          placeholder="Song requests (comma-separated)"
          value={form?.songs || ""}
          onChange={(e) => setField("songs", e.target.value)}
        />

        {/* Plus One */}
        <div className="card p-4 bg-white/70 border border-white/60 rounded-xl">
          <label className="flex items-center gap-3">
            <input
              type="checkbox"
              checked={!!form?.has_plus_one}
              onChange={(e) => togglePlusOne(e.target.checked)}
            />
            <span className="font-semibold text-soil-700">Bringing a plus one?</span>
          </label>
          {form?.has_plus_one && (
            <div className="mt-3 grid md:grid-cols-2 gap-4">
              <input
                className="input"
                placeholder="Plus One Name"
                value={form?.plus_one_name || ""}
                onChange={(e) => setField("plus_one_name", e.target.value)}
              />
              <input
                className="input"
                placeholder="Plus One Dietary"
                value={form?.plus_one_dietary || ""}
                onChange={(e) => setField("plus_one_dietary", e.target.value)}
              />
            </div>
          )}
        </div>

        {/* Children */}
        <div className="card p-4 bg-white/70 border border-white/60 rounded-xl">
          <label className="flex items-center gap-3">
            <input
              type="checkbox"
              checked={!!form?.has_children}
              onChange={(e) => toggleChildren(e.target.checked)}
            />
            <span className="font-semibold text-soil-700">Bringing children?</span>
          </label>
          {form?.has_children && (
            <div className="mt-3 space-y-3">
              {(form?.children || []).map((c, i) => (
                <div key={i} className="grid md:grid-cols-2 gap-3">
                  <input
                    className="input"
                    placeholder="Child Name"
                    value={c.name || ""}
                    onChange={(e) => updateChild(i, "name", e.target.value)}
                  />
                  <div className="flex gap-3">
                    <input
                      className="input flex-1"
                      placeholder="Child Dietary"
                      value={c.dietary || ""}
                      onChange={(e) => updateChild(i, "dietary", e.target.value)}
                    />
                    <button
                      type="button"
                      className="btn-outline"
                      onClick={() => removeChild(i)}
                      title="Remove child"
                    >
                      Remove
                    </button>
                  </div>
                </div>
              ))}
              <button type="button" className="btn-outline" onClick={addChild}>
                + Add Child
              </button>
            </div>
          )}
        </div>

        <div className="flex gap-3 justify-end pt-2">
          {!isCreate && (
            <button
              className="btn-outline"
              onClick={() => {
                setForm(rsvp);
                setEdit(false);
                setFormErr("");
              }}
              type="button"
            >
              Cancel
            </button>
          )}
          <button className="btn-primary" onClick={save} type="button">
            {isCreate ? "Submit" : "Save"}
          </button>
        </div>
      </Card>
    </Backdrop>
  );
}

/** Helpers */
function normalizeRsvp(row) {
  return {
    ...row,
    attending: !!row.attending,
    has_plus_one: !!row.has_plus_one,
    has_children: !!row.has_children,
    children: Array.isArray(row.children) ? row.children : [],
  };
}

function getCountdownText(target) {
  const now = dayjs();
  if (!target?.isValid?.() || now.isAfter(target)) return "It's wedding time! ðŸŽ‰";
  const diff = dayjs.duration(target.diff(now));
  const d = Math.floor(diff.asDays());
  const h = diff.hours().toString().padStart(2, "0");
  const m = diff.minutes().toString().padStart(2, "0");
  const s = diff.seconds().toString().padStart(2, "0");
  return `${d} days â€¢ ${h}:${m}:${s}`;
}

