// File: src/pages/UploadMediaPage.jsx
import GoogleDrivePicker from "../components/GoogleDrivePicker";

const PHOTOS_FOLDER = process.env.REACT_APP_GDRIVE_PHOTOS_FOLDER;
const VIDEOS_FOLDER = process.env.REACT_APP_GDRIVE_VIDEOS_FOLDER;

export default function UploadMediaPage() {
  return (
    <div className="min-h-screen bg-gray-950 text-gray-100 px-4 py-10">
      <div className="max-w-3xl mx-auto space-y-6">
        <h1 className="text-3xl font-bold">Upload Wedding Media</h1>
        <p className="text-gray-300">
          Please upload your photos and videos. Filenames will include your Google display name so we can show who uploaded what.
        </p>

        <div className="flex gap-3 flex-wrap">
          <GoogleDrivePicker targetFolderId={PHOTOS_FOLDER} buttonLabel="Upload Photos" />
          <GoogleDrivePicker targetFolderId={VIDEOS_FOLDER} buttonLabel="Upload Videos" />
        </div>

        <p className="text-sm text-gray-400">
          Tip: Larger videos may take a moment to process on Google Drive before previews work.
        </p>
      </div>
    </div>
  );
}
