import { Routes, Route } from "react-router-dom";

// ‚úÖ Make sure this path matches your actual file name
import NavigationBar from "../components/Navbar";

import LoginPage from "../pages/LoginPage";
import SignupPage from "../pages/SignupPage";
import ResetPasswordPage from "../pages/ResetPasswordPage";
import ResetPasswordUpdatePage from "../pages/ResetPasswordUpdatePage";

import RSVPPage from "../pages/RSVPPage";
import ViewRSVPPage from "../pages/ViewRSVPPage";
import UploadMediaPage from "../pages/UploadMediaPage";
import WeddingAlbumPage from "../pages/WeddingAlbumPage";
import LeaveMessagePage from "../pages/LeaveMessagePage";
import MessageWallPage from "../pages/MessageWallPage";
import VenuePage from "../pages/VenuePage";

import AdminDashboard from "../pages/AdminDashboard";

import ProtectedRoute from "../components/ProtectedRoute";
import RoleRoute from "../components/RoleRoute";

export default function AppRoutes() {
  return (
    <>
      <NavigationBar />
      <Routes>
        {/* Public */}
        <Route path="/" element={<RSVPPage />} />
        <Route path="/rsvp" element={<RSVPPage />} />
        <Route path="/album" element={<WeddingAlbumPage />} />
        <Route path="/leave-a-message" element={<LeaveMessagePage />} />
        <Route path="/message-wall" element={<MessageWallPage />} />
        <Route path="/venue" element={<VenuePage />} />

        {/* Auth pages (still public, just not in the navbar) */}
        <Route path="/login" element={<LoginPage />} />
        <Route path="/signup" element={<SignupPage />} />
        <Route path="/reset" element={<ResetPasswordPage />} />
        {/* üîê Supabase redirects here from email to set a new password */}
        <Route path="/reset-password" element={<ResetPasswordUpdatePage />} />

        {/* Authed */}
        <Route
          path="/my-rsvp"
          element={
            <ProtectedRoute>
              <ViewRSVPPage />
            </ProtectedRoute>
          }
        />
        <Route
          path="/upload-media"
          element={
            <ProtectedRoute>
              <UploadMediaPage />
            </ProtectedRoute>
          }
        />

        {/* Admin */}
        <Route
          path="/admin"
          element={
            <RoleRoute roles={["admin"]}>
              <AdminDashboard />
            </RoleRoute>
          }
        />
      </Routes>
    </>
  );
}
