// File: src/pages/WeddingAlbumPage.jsx
import { useEffect, useMemo, useState } from "react";
import { supabase } from "../supabaseClient";
import dayjs from "dayjs";

const PAGE_SIZE = 24;

export default function WeddingAlbumPage() {
  const [items, setItems] = useState([]);
  const [page, setPage] = useState(0);
  const [hasMore, setHasMore] = useState(true);
  const [loading, setLoading] = useState(false);

  const loadPage = async () => {
    if (loading || !hasMore) return;
    setLoading(true);
    const from = page * PAGE_SIZE;
    const to = from + PAGE_SIZE - 1;

    const { data, error } = await supabase
      .from("album_files")
      .select("*")
      .order("created_at", { ascending: false })
      .range(from, to);

    if (error) {
      console.error(error);
    } else {
      setItems((prev) => [...prev, ...(data || [])]);
      if (!data || data.length < PAGE_SIZE) setHasMore(false);
      setPage((p) => p + 1);
    }
    setLoading(false);
  };

  useEffect(() => {
    loadPage();
    // eslint-disable-next-line
  }, []);

  // Group by yyyy-mm-dd
  const groups = useMemo(() => {
    const map = {};
    for (const it of items) {
      const key = dayjs(it.created_at).format("YYYY-MM-DD");
      if (!map[key]) map[key] = [];
      map[key].push(it);
    }
    return Object.entries(map).sort(([a], [b]) => (a < b ? 1 : -1));
  }, [items]);

  // Infinite scroll
  useEffect(() => {
    const onScroll = () => {
      const nearBottom = window.innerHeight + window.scrollY >= document.body.offsetHeight - 300;
      if (nearBottom) loadPage();
    };
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
    // eslint-disable-next-line
  }, [page, hasMore, loading]);

  return (
    <div className="min-h-screen bg-gray-950 text-gray-100 px-4 py-8">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-3xl font-bold mb-6">Wedding Album</h1>
        <div className="space-y-8">
          {groups.map(([date, arr]) => (
            <section key={date}>
              <h2 className="text-xl font-semibold mb-3">{dayjs(date).format("DD MMM YYYY")}</h2>
              <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-3">
                {arr.map((it) => (
                  <a
                    key={it.id}
                    href={it.preview_url}
                    target="_blank"
                    rel="noreferrer"
                    className="block group rounded-xl overflow-hidden bg-gray-900 border border-gray-800"
                    title={`${it.drive_name} â€” Uploaded by ${it.uploader_name || "Guest"}`}
                  >
                    {it.media_type === "image" ? (
                      <img
                        src={it.preview_url}
                        alt={it.drive_name}
                        className="w-full h-40 object-cover group-hover:opacity-90"
                        loading="lazy"
                      />
                    ) : it.media_type === "video" ? (
                      <div className="w-full h-40 flex items-center justify-center text-sm text-gray-300">
                        Open video â†—
                      </div>
                    ) : (
                      <div className="w-full h-40 flex items-center justify-center text-sm text-gray-400">
                        File
                      </div>
                    )}
                    <div className="p-2 text-xs text-gray-300 flex justify-between">
                      <span className="truncate">{it.uploader_name || "Guest"}</span>
                      <span>{dayjs(it.created_at).format("HH:mm")}</span>
                    </div>
                  </a>
                ))}
              </div>
            </section>
          ))}
        </div>

        {loading && <p className="mt-6 text-gray-400">Loadingâ€¦</p>}
        {!hasMore && <p className="mt-6 text-gray-500">End of album.</p>}
      </div>
    </div>
  );
}
