import { useEffect, useState } from "react";
import { supabase } from "../supabaseClient";
import { toast } from "react-hot-toast";
import Papa from "papaparse";
import jsPDF from "jspdf";

export default function AdminDashboard() {
  const [rows, setRows] = useState([]);
  const [q, setQ] = useState("");

  useEffect(() => { (async ()=>{
      const { data, error } = await supabase.from("rsvps").select("*").order("created_at",{ascending:false});
      if (!error && data) setRows(data);
    })();
  }, []);

  const filtered = rows.filter(r => [r.full_name, r.email, r.dietary, r.songs].join(" ").toLowerCase().includes(q.toLowerCase()));

  const del = async (id) => {
    if (!window.confirm("Delete this RSVP?")) return;
    const { error } = await supabase.from("rsvps").delete().eq("id", id);
    if (error) return toast.error(error.message);
    setRows(rows.filter(r=>r.id!==id)); toast.success("Deleted");
  };

  const exportCSV = () => {
    const csv = Papa.unparse(rows);
    const blob = new Blob([csv], { type: "text/csv;charset=utf-8;" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a"); a.href = url; a.download = "rsvps.csv"; a.click(); URL.revokeObjectURL(url);
  };

  const exportPDF = () => {
    const doc = new jsPDF(); doc.text("RSVPs - Abraham & Jesse-Lee", 14, 16);
    let y = 24; filtered.slice(0,50).forEach((r,i)=>{ doc.text(`${i+1}. ${r.full_name} | ${r.email} | ${r.attending?"Attending":"Not"} | ${r.dietary||"-"}`, 14, y); y += 8; if (y>280){doc.addPage(); y=16;} });
    doc.save("rsvps.pdf");
  };

  return (
    <div className="max-w-6xl mx-auto p-4">
      <header className="my-6 flex flex-wrap gap-3 items-center justify-between">
        <h1 className="text-3xl font-extrabold text-soil-700">Admin Dashboard ðŸŒ»</h1>
        <div className="flex gap-2">
          <button className="btn-outline" onClick={exportCSV}>Export CSV</button>
          <button className="btn-outline" onClick={exportPDF}>Export PDF</button>
        </div>
      </header>
      <div className="card p-4 mb-4"><input className="input" placeholder="Search name/email/dietary/song..." value={q} onChange={e=>setQ(e.target.value)} /></div>
      <div className="card p-0 overflow-x-auto">
        <table className="min-w-full text-sm">
          <thead className="bg-sunflower-100 text-soil-700">
            <tr><th className="text-left p-3">Name</th><th className="text-left p-3">Email</th><th className="text-left p-3">Attending</th><th className="text-left p-3">Dietary</th><th className="text-left p-3">Songs</th><th className="text-right p-3">Actions</th></tr>
          </thead>
          <tbody>
            {filtered.map(r=>(
              <tr key={r.id} className="border-t border-sunflower-100">
                <td className="p-3">{r.full_name}</td>
                <td className="p-3">{r.email}</td>
                <td className="p-3">{r.attending ? "Yes" : "No"}</td>
                <td className="p-3">{r.dietary || "-"}</td>
                <td className="p-3">{r.songs || "-"}</td>
                <td className="p-3 text-right">
                  <button className="btn-outline mr-2" onClick={()=>alert("TODO: open edit modal")}>Edit</button>
                  <button className="btn-primary" onClick={()=>del(r.id)}>Delete</button>
                </td>
              </tr>
            ))}
            {!filtered.length && <tr><td className="p-4 text-center text-soil-500" colSpan={6}>No results</td></tr>}
          </tbody>
        </table>
      </div>
    </div>
  );
}
