import { Link } from "react-router-dom";
import { supabase } from "../supabaseClient";
import { useEffect, useState } from "react";

export default function NavigationBar() {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState(localStorage.getItem("role") || "user");

  useEffect(() => {
    supabase.auth.getUser().then(({ data }) => setUser(data?.user || null));
  }, []);

  const logout = async () => {
    await supabase.auth.signOut();
    localStorage.removeItem("role");
    window.location.href = "/";
  };

  return (
    <nav className="w-full bg-white/80 backdrop-blur border-b border-sunflower-100 sticky top-0 z-40">
      <div className="max-w-6xl mx-auto px-4 py-3 flex items-center justify-between">
        <Link to="/" className="flex items-center gap-2">
          <span className="text-2xl font-extrabold text-sunflower-700">ðŸŒ»</span>
          <span className="font-bold text-soil-700">Abraham & Jesse-Lee</span>
        </Link>

        <div className="flex items-center gap-3">
          <Link className="btn-outline" to="/rsvp">RSVP</Link>
          <Link className="btn-outline" to="/album">Album</Link>
          <Link className="btn-outline" to="/leave-a-message">Leave a Message</Link>
          <Link className="btn-outline" to="/message-wall">Message Wall</Link>
          <Link className="btn-outline" to="/venue">Venue</Link>
          {role === "admin" && <Link className="btn-outline" to="/admin">Admin</Link>}
          {!user ? (
            <>
              <Link className="btn-primary" to="/login">Login</Link>
              <Link className="btn-outline" to="/signup">Sign Up</Link>
            </>
          ) : (
            <button className="btn-primary" onClick={logout}>Logout</button>
          )}
        </div>
      </div>
    </nav>
  );
}
