// File: src/pages/WeddingAlbumPage.jsx
import { useCallback, useEffect, useMemo, useState } from "react";
import dayjs from "dayjs";

const BG_URL =
  "https://cyxeucwesqzwlisgveiz.supabase.co/storage/v1/object/public/public-assets/flowers-8309997.jpg";

// env helpers
const fromEnv = (v) => (v ? String(v).split("#")[0].replace(/^["']|["']$/g, "").trim() : "");
const API_KEY = fromEnv(process.env.REACT_APP_GOOGLE_API_KEY);
const PHOTOS_FOLDER = fromEnv(process.env.REACT_APP_DRIVE_PHOTOS_FOLDER_ID);
const VIDEOS_FOLDER = fromEnv(process.env.REACT_APP_DRIVE_VIDEOS_FOLDER_ID);

const PAGE_SIZE = 48;

function inferType(mime, name = "") {
  const m = (mime || "").toLowerCase();
  if (m.startsWith("image/")) return "image";
  if (m.startsWith("video/")) return "video";
  if (/\.(png|jpe?g|gif|webp|bmp|heic)$/i.test(name)) return "image";
  if (/\.(mp4|mov|webm|mkv|avi)$/i.test(name)) return "video";
  return "file";
}
const viewURL = (id) =>
  `https://drive.google.com/uc?export=view&id=${id}&rl`; // &rl forces refresh
const downloadURL = (id) => `https://drive.google.com/uc?export=download&id=${id}`;

export default function WeddingAlbumPage() {
  const [tab, setTab] = useState("photos");
  const [items, setItems] = useState([]);
  const [nextToken, setNextToken] = useState(null);
  const [loading, setLoading] = useState(false);
  const [err, setErr] = useState("");

  const currentFolder = tab === "photos" ? PHOTOS_FOLDER : VIDEOS_FOLDER;

  const listFolder = useCallback(
    async (folderId, pageToken) => {
      const url = new URL("https://www.googleapis.com/drive/v3/files");
      url.searchParams.set("q", `'${folderId}' in parents and trashed = false`);
      url.searchParams.set(
        "fields",
        "nextPageToken, files(id,name,mimeType,createdTime,thumbnailLink,webViewLink)"
      );
      url.searchParams.set("orderBy", "createdTime desc");
      url.searchParams.set("pageSize", String(PAGE_SIZE));
      url.searchParams.set("includeItemsFromAllDrives", "true");
      url.searchParams.set("supportsAllDrives", "true");
      if (pageToken) url.searchParams.set("pageToken", pageToken);
      url.searchParams.set("key", API_KEY);

      const res = await fetch(url.toString());
      const json = await res.json();
      if (!res.ok) throw new Error(json.error?.message || "Drive API error");
      const files = (json.files || []).map((f) => ({
        id: f.id,
        drive_name: f.name,
        mimeType: f.mimeType,
        media_type: inferType(f.mimeType, f.name),
        created_at: f.createdTime,
        preview_url: viewURL(f.id),
        webViewLink: f.webViewLink,
        thumb: f.thumbnailLink,
      }));
      return { files, nextPageToken: json.nextPageToken || null };
    },
    []
  );

  const loadInitial = useCallback(async () => {
    if (!API_KEY || !currentFolder) return;
    setErr("");
    setLoading(true);
    try {
      const { files, nextPageToken } = await listFolder(currentFolder, null);
      setItems(files);
      setNextToken(nextPageToken);
    } catch (e) {
      setErr(e.message);
    } finally {
      setLoading(false);
    }
  }, [API_KEY, currentFolder, listFolder]);

  const loadMore = useCallback(async () => {
    if (loading || !nextToken) return;
    setLoading(true);
    try {
      const { files, nextPageToken } = await listFolder(currentFolder, nextToken);
      setItems((prev) => [...prev, ...files]);
      setNextToken(nextPageToken);
    } catch (e) {
      setErr(e.message);
    } finally {
      setLoading(false);
    }
  }, [currentFolder, listFolder, loading, nextToken]);

  // initial + when switching tabs
  useEffect(() => {
    setItems([]);
    setNextToken(null);
    loadInitial();
    // eslint-disable-next-line
  }, [tab]);

  // group by date
  const groups = useMemo(() => {
    const map = {};
    for (const it of items) {
      const key = dayjs(it.created_at).format("YYYY-MM-DD");
      (map[key] ||= []).push(it);
    }
    return Object.entries(map).sort(([a], [b]) => (a < b ? 1 : -1));
  }, [items]);

  return (
    <Backdrop>
      <Card>
        <header className="mb-6">
          <h1 className="section-title">Wedding Album</h1>
          <p className="text-soil-700">
            Direct download enabled. New uploads appear once Google Drive finishes processing.
          </p>
        </header>

        <div className="flex gap-2 mb-4">
          <button
            className={`btn-outline ${tab === "photos" ? "bg-white/80" : ""}`}
            onClick={() => setTab("photos")}
          >
            Photos
          </button>
          <button
            className={`btn-outline ${tab === "videos" ? "bg-white/80" : ""}`}
            onClick={() => setTab("videos")}
          >
            Videos
          </button>
        </div>

        {(!API_KEY || !PHOTOS_FOLDER || !VIDEOS_FOLDER) && (
          <div className="text-sm text-amber-700 bg-amber-50 border border-amber-200 rounded-md p-2 mb-4">
            Missing envs. Set <code>REACT_APP_GOOGLE_API_KEY</code>,{" "}
            <code>REACT_APP_DRIVE_PHOTOS_FOLDER_ID</code>,{" "}
            <code>REACT_APP_DRIVE_VIDEOS_FOLDER_ID</code> and restart the dev server.
          </div>
        )}

        <div className="text-xs text-soil-600 mb-4">
          Make sure both folders are shared: <span className="font-medium">Anyone with the link → Viewer</span>.
        </div>

        {err && (
          <div className="text-sm text-red-700 bg-red-50 border border-red-200 rounded-md p-2 mb-4">
            {err}
          </div>
        )}

        {items.length === 0 && !loading && !err && (
          <p className="text-soil-700">No media yet.</p>
        )}

        <div className="space-y-8">
          {groups.map(([date, arr]) => (
            <section key={date}>
              <h2 className="text-lg font-semibold text-soil-800 mb-3">
                {dayjs(date).format("DD MMM YYYY")}
              </h2>
              <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-3">
                {arr.map((it) => (
                  <AlbumCard key={it.id} item={it} />
                ))}
              </div>
            </section>
          ))}
        </div>

        <div className="mt-6 flex items-center gap-3">
          {loading && <span className="text-soil-600">Loading…</span>}
          {!loading && nextToken && (
            <button className="btn-outline" onClick={loadMore}>
              Load more
            </button>
          )}
          {!loading && !nextToken && items.length > 0 && (
            <span className="text-soil-600">End of album.</span>
          )}
        </div>
      </Card>
    </Backdrop>
  );
}

function AlbumCard({ item }) {
  const { media_type, preview_url, drive_name, created_at, id, webViewLink } = item;
  const time = dayjs(created_at).format("HH:mm");
  const downloadHref = downloadURL(id);
  const openHref = media_type === "image" ? preview_url : webViewLink;

  return (
    <div className="rounded-xl overflow-hidden bg-white/70 border border-white/60">
      <a
        href={openHref}
        target="_blank"
        rel="noreferrer"
        title={drive_name}
        className="block group"
      >
        {media_type === "image" ? (
          <img
            src={preview_url}
            alt={drive_name}
            className="w-full h-40 object-cover group-hover:opacity-90"
            loading="lazy"
          />
        ) : media_type === "video" ? (
          <div className="w-full h-40 flex items-center justify-center text-sm text-soil-700">
            Open video ↗
          </div>
        ) : (
          <div className="w-full h-40 flex items-center justify-center text-sm text-soil-600">
            Open file ↗
          </div>
        )}
      </a>
      <div className="p-2 text-xs text-soil-700 flex items-center justify-between gap-2">
        <span className="truncate">{drive_name}</span>
        <span>{time}</span>
      </div>
      <div className="p-2 pt-0 flex gap-2 justify-end">
        <a
          className="btn-outline text-xs"
          href={downloadHref}
          target="_blank"
          rel="noreferrer"
        >
          Download
        </a>
      </div>
    </div>
  );
}

/** UI wrappers */
