// File: src/pages/ResetPasswordPage.jsx
import { useState } from "react";
import { useAuth } from "../context/AuthProvider";
import { toast } from "react-hot-toast";

export default function ResetPasswordPage() {
  const [email, setEmail] = useState("");
  const [loading, setLoading] = useState(false);
  const [formErr, setFormErr] = useState("");
  const { resetPassword } = useAuth();

  const submit = async (e) => {
    e.preventDefault();
    setFormErr("");
    if (!email) return setFormErr("Please enter your email.");
    if (!/^\S+@\S+\.\S+$/.test(email)) return setFormErr("Enter a valid email.");

    setLoading(true);
    try {
      const redirectTo = `${window.location.origin}/reset-password`;
      await resetPassword({ email: email.trim(), redirectTo });
      toast.success("Password reset link sent! Check your email.");
    } catch (err) {
      const msg = err?.message || "Could not send reset email.";
      setFormErr(msg);
      toast.error(msg);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-[70vh] flex items-center justify-center p-4">
      <form onSubmit={submit} className="card p-6 w-full max-w-md">
        <h1 className="section-title text-center">Reset Password ðŸŒ»</h1>

        {!!formErr && (
          <div className="mt-3 text-sm text-red-600 bg-red-50 border border-red-200 rounded-md p-2">
            {formErr}
          </div>
        )}

        <div className="mt-4 space-y-3">
          <input
            className="input"
            type="email"
            placeholder="Email"
            autoComplete="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            disabled={loading}
            required
          />
          <button className="btn-primary w-full" type="submit" disabled={loading}>
            {loading ? "Sendingâ€¦" : "Send Reset Link"}
          </button>
        </div>
      </form>
    </div>
  );
}
