import { useState } from "react";
import { useAuth } from "../context/AuthProvider";
import { toast } from "react-hot-toast";
import { Link } from "react-router-dom";

export default function SignupPage() {
  const [email,setEmail]=useState(""); const [password,setPassword]=useState("");
  const { signUp } = useAuth();
  const submit = async (e)=>{
    e.preventDefault();
    try { await signUp({ email, password }); toast.success("Check your email to confirm!"); }
    catch(err){ toast.error(err.message); }
  };
  return (
    <div className="min-h-[70vh] flex items-center justify-center p-4">
      <form onSubmit={submit} className="card p-6 w-full max-w-md">
        <h1 className="section-title text-center">Create Account ðŸŒ»</h1>
        <div className="mt-4 space-y-3">
          <input className="input" type="email" placeholder="Email" value={email} onChange={e=>setEmail(e.target.value)} />
          <input className="input" type="password" placeholder="Password" value={password} onChange={e=>setPassword(e.target.value)} />
          <button className="btn-primary w-full" type="submit">Sign Up</button>
          <div className="text-center text-sm mt-2">
            <Link to="/login" className="text-soil-600 hover:underline">Have an account? Login</Link>
          </div>
        </div>
      </form>
    </div>
  );
}
