import { useMemo, useState } from "react";
import { supabase } from "../supabaseClient";
import { toast } from "react-hot-toast";

const BG_URL =
  "https://cyxeucwesqzwlisgveiz.supabase.co/storage/v1/object/public/public-assets/flowers-8309997.jpg";

export default function RSVPPage() {
  const [form, setForm] = useState({
    full_name: "",
    email: "",
    attending: true,
    dietary: "",
    songs: "",
    has_plus_one: false,
    plus_one_name: "",
    plus_one_dietary: "",
    has_children: false,
    children: [],
  });
  const [behalfList, setBehalfList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [formErr, setFormErr] = useState("");

  const isValidEmail = useMemo(
    () => (v) => /^\S+@\S+\.\S+$/.test(v || ""),
    []
  );

  const setField = (key, val) => setForm((f) => ({ ...f, [key]: val }));

  const togglePlusOne = (checked) => {
    setForm((f) => ({
      ...f,
      has_plus_one: checked,
      ...(checked
        ? {}
        : { plus_one_name: "", plus_one_dietary: "" }),
    }));
  };

  const toggleChildren = (checked) => {
    setForm((f) => ({
      ...f,
      has_children: checked,
      ...(checked ? {} : { children: [] }),
    }));
  };

  const addChild = () =>
    setForm((f) => ({
      ...f,
      children: [...f.children, { name: "", dietary: "" }],
    }));

  const updateChild = (i, key, val) =>
    setForm((f) => {
      const copy = [...f.children];
      copy[i] = { ...copy[i], [key]: val };
      return { ...f, children: copy };
    });

  const addBehalfGuest = () =>
    setBehalfList((l) => [
      ...l,
      { full_name: "", attending: true, dietary: "", songs: "" },
    ]);

  const updateBehalfGuest = (i, key, val) =>
    setBehalfList((l) => {
      const copy = [...l];
      copy[i] = { ...copy[i], [key]: val };
      return copy;
    });

  const submit = async (e) => {
    e.preventDefault();
    setFormErr("");

    // Basic validation
    if (!form.full_name?.trim()) return setFormErr("Please enter your full name.");
    if (!isValidEmail(form.email)) return setFormErr("Please enter a valid email address.");

    // Normalize payload
    const payload = {
      ...form,
      full_name: form.full_name.trim(),
      email: form.email.trim().toLowerCase(),
      attending: !!form.attending,
      has_plus_one: !!form.has_plus_one,
      has_children: !!form.has_children,
      submitted_by: form.email.trim().toLowerCase(),
    };

    // If toggles off, ensure related fields are cleared
    if (!payload.has_plus_one) {
      payload.plus_one_name = "";
      payload.plus_one_dietary = "";
    }
    if (!payload.has_children) {
      payload.children = [];
    }

    setLoading(true);
    try {
      // Insert main RSVP
      const { error } = await supabase.from("rsvps").insert([payload]);
      if (error) throw error;

      // Insert behalf-of RSVPs (stored as separate entries, user_id = null)
      for (const g of behalfList) {
        if (!g.full_name?.trim()) continue; // skip empty rows
        const gPayload = {
          ...g,
          full_name: g.full_name.trim(),
          attending: !!g.attending,
          submitted_by: payload.email,
          user_id: null,
          email: null, // behalf guests may not have email
        };
        const { error: e2 } = await supabase.from("rsvps").insert([gPayload]);
        if (e2) throw e2;
      }

      toast.success("RSVP submitted! ðŸŒ»");
      // Reset minimal fields; keep toggles
      setForm((f) => ({
        ...f,
        full_name: "",
        email: "",
        dietary: "",
        songs: "",
        plus_one_name: f.has_plus_one ? "" : "",
        plus_one_dietary: f.has_plus_one ? "" : "",
        children: f.has_children ? [] : [],
      }));
      setBehalfList([]);
    } catch (err) {
      toast.error(err?.message || "Could not submit RSVP. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      className="min-h-screen w-full bg-center bg-cover relative"
      style={{ backgroundImage: `url(${BG_URL})` }}
    >
      {/* Soft grey overlay to match auth pages */}
      <div className="absolute inset-0 bg-black/30 backdrop-blur-[1px]" />

      <div className="relative z-10 max-w-5xl mx-auto px-4 py-10">
        {/* Header */}
        <header className="text-center my-6 text-white drop-shadow">
          <h1 className="text-3xl font-extrabold">
            RSVP for <span className="text-yellow-200">Abraham & Jesseâ€‘Lee</span>
          </h1>
          <p className="text-white/90">We canâ€™t wait to celebrate with you.</p>
        </header>

        {/* Card */}
        <form onSubmit={submit} className="bg-white/85 rounded-2xl shadow-xl border border-white/60 p-6 md:p-8 space-y-4">
          {!!formErr && (
            <div className="text-sm text-red-600 bg-red-50 border border-red-200 rounded-md p-2">
              {formErr}
            </div>
          )}

          <div className="grid md:grid-cols-2 gap-4">
            <input
              className="input"
              placeholder="Full Name"
              value={form.full_name}
              onChange={(e) => setField("full_name", e.target.value)}
              required
            />
            <input
              className="input"
              type="email"
              placeholder="Email"
              value={form.email}
              onChange={(e) => setField("email", e.target.value)}
              required
            />
          </div>

          <div className="grid md:grid-cols-2 gap-4">
            <select
              className="input"
              value={String(form.attending)}
              onChange={(e) => setField("attending", e.target.value === "true")}
            >
              <option value="true">Attending</option>
              <option value="false">Not Attending</option>
            </select>
            <input
              className="input"
              placeholder="Dietary Requirements"
              value={form.dietary}
              onChange={(e) => setField("dietary", e.target.value)}
            />
          </div>

          <input
            className="input"
            placeholder="Song requests (comma-separated)"
            value={form.songs}
            onChange={(e) => setField("songs", e.target.value)}
          />

          {/* Plus One */}
          <div className="card p-4 bg-white/70 border border-white/60 rounded-xl">
            <label className="flex items-center gap-3">
              <input
                type="checkbox"
                checked={form.has_plus_one}
                onChange={(e) => togglePlusOne(e.target.checked)}
              />
              <span className="font-semibold text-soil-700">Bringing a plus one?</span>
            </label>
            {form.has_plus_one && (
              <div className="mt-3 grid md:grid-cols-2 gap-4">
                <input
                  className="input"
                  placeholder="Plus One Name"
                  value={form.plus_one_name}
                  onChange={(e) => setField("plus_one_name", e.target.value)}
                />
                <input
                  className="input"
                  placeholder="Plus One Dietary"
                  value={form.plus_one_dietary}
                  onChange={(e) => setField("plus_one_dietary", e.target.value)}
                />
              </div>
            )}
          </div>

          {/* Children */}
          <div className="card p-4 bg-white/70 border border-white/60 rounded-xl">
            <label className="flex items-center gap-3">
              <input
                type="checkbox"
                checked={form.has_children}
                onChange={(e) => toggleChildren(e.target.checked)}
              />
              <span className="font-semibold text-soil-700">Bringing children?</span>
            </label>
            {form.has_children && (
              <div className="mt-3 space-y-3">
                {form.children.map((c, i) => (
                  <div key={i} className="grid md:grid-cols-2 gap-3">
                    <input
                      className="input"
                      placeholder="Child Name"
                      value={c.name}
                      onChange={(e) => updateChild(i, "name", e.target.value)}
                    />
                    <input
                      className="input"
                      placeholder="Child Dietary"
                      value={c.dietary}
                      onChange={(e) => updateChild(i, "dietary", e.target.value)}
                    />
                  </div>
                ))}
                <button
                  type="button"
                  className="btn-outline"
                  onClick={addChild}
                >
                  + Add Child
                </button>
              </div>
            )}
          </div>

          {/* RSVP on behalf of others */}
          <div className="card p-4 bg-white/70 border border-white/60 rounded-xl">
            <div className="flex items-center justify-between">
              <h3 className="font-bold text-soil-700">RSVP on behalf of others</h3>
              <button
                type="button"
                className="btn-outline"
                onClick={addBehalfGuest}
              >
                + Add Guest
              </button>
            </div>
            <div className="mt-3 space-y-3">
              {behalfList.map((g, idx) => (
                <div key={idx} className="grid md:grid-cols-4 gap-3">
                  <input
                    className="input"
                    placeholder="Full Name"
                    value={g.full_name}
                    onChange={(e) => updateBehalfGuest(idx, "full_name", e.target.value)}
                  />
                  <select
                    className="input"
                    value={String(g.attending)}
                    onChange={(e) => updateBehalfGuest(idx, "attending", e.target.value === "true")}
                  >
                    <option value="true">Attending</option>
                    <option value="false">Not Attending</option>
                  </select>
                  <input
                    className="input"
                    placeholder="Dietary"
                    value={g.dietary}
                    onChange={(e) => updateBehalfGuest(idx, "dietary", e.target.value)}
                  />
                  <input
                    className="input"
                    placeholder="Songs"
                    value={g.songs}
                    onChange={(e) => updateBehalfGuest(idx, "songs", e.target.value)}
                  />
                </div>
              ))}
            </div>
          </div>

          <div className="flex justify-end">
            <button className="btn-primary" type="submit" disabled={loading}>
              {loading ? "Submittingâ€¦" : "Submit RSVP"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
