// File: src/context/AuthProvider.jsx
import { createContext, useContext, useEffect, useMemo, useRef, useCallback, useState } from "react";
import { supabase } from "../supabaseClient";

async function fetchRoleFromProfiles(userId) {
  if (!userId) return "user";
  const { data, error } = await supabase
    .from("profiles")
    .select("is_admin")
    .eq("user_id", userId) // Make sure this matches your DB column
    .single();

  if (error) {
    console.error("[Auth] Error fetching profile role:", error);
    return "user";
  }
  return data?.is_admin ? "admin" : "user";
}

const AuthContext = createContext({
  user: null,
  role: "user",
  loading: true,
  signIn: async () => {},
  signUp: async () => {},
  signOut: async () => {},
  resetPassword: async () => {},
  updateProfile: async () => {},
});

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState("user");
  const [loading, setLoading] = useState(true);
  const mountedRef = useRef(true);

  // Load user + role from Supabase
  useEffect(() => {
    mountedRef.current = true;

    (async () => {
      try {
        const { data: { session }, error } = await supabase.auth.getSession();
        if (error) console.error("[Auth] getSession error:", error);
        const u = session?.user ?? null;
        if (!mountedRef.current) return;

        setUser(u);
        const fetchedRole = u ? await fetchRoleFromProfiles(u.id) : "user";
        setRole(fetchedRole);
      } finally {
        if (mountedRef.current) setLoading(false);
      }
    })();

    const { data: subscription } = supabase.auth.onAuthStateChange(
      async (_event, session) => {
        if (!mountedRef.current) return;
        const u = session?.user ?? null;
        setUser(u);
        const fetchedRole = u ? await fetchRoleFromProfiles(u.id) : "user";
        setRole(fetchedRole);
        setLoading(false);
      }
    );

    return () => {
      mountedRef.current = false;
      subscription?.subscription?.unsubscribe?.();
    };
  }, []);

  const signIn = useCallback(async ({ email, password }) => {
    const { error } = await supabase.auth.signInWithPassword({ email, password });
    if (error) throw error;
  }, []);

  const signUp = useCallback(async ({ email, password, metadata = {} }) => {
    const { error } = await supabase.auth.signUp({
      email,
      password,
      options: { data: metadata },
    });
    if (error) throw error;
  }, []);

  const signOut = useCallback(async () => {
    const { error } = await supabase.auth.signOut();
    if (error) throw error;
  }, []);

  const resetPassword = useCallback(async ({ email, redirectTo }) => {
    const { error } = await supabase.auth.resetPasswordForEmail(email, {
      redirectTo,
    });
    if (error) throw error;
  }, []);

  const updateProfile = useCallback(async (updates = {}) => {
    const { data, error } = await supabase.auth.updateUser({ data: updates });
    if (error) throw error;
    const u = data?.user ?? null;
    setUser(u);
    const fetchedRole = u ? await fetchRoleFromProfiles(u.id) : "user";
    setRole(fetchedRole);
  }, []);

  const value = useMemo(
    () => ({
      user,
      role,
      loading,
      signIn,
      signUp,
      signOut,
      resetPassword,
      updateProfile,
    }),
    [user, role, loading, signIn, signUp, signOut, resetPassword, updateProfile]
  );

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}

export function useAuth() {
  return useContext(AuthContext);
}
