import { useState } from "react";
import { supabase } from "../supabaseClient";
import { toast } from "react-hot-toast";

export default function RSVPPage() {
  const [form, setForm] = useState({
    full_name: "", email: "", attending: true, dietary: "", songs: "",
    has_plus_one: false, plus_one_name: "", plus_one_dietary: "",
    has_children: false, children: [],
  });
  const [behalfList, setBehalfList] = useState([]);

  const submit = async (e)=>{
    e.preventDefault();
    try {
      const { error } = await supabase.from("rsvps").insert([{ ...form, submitted_by: form.email }]);
      if (error) throw error;
      for (const g of behalfList) {
        const { error: e2 } = await supabase.from("rsvps").insert([{ ...g, user_id: null, submitted_by: form.email }]);
        if (e2) throw e2;
      }
      toast.success("RSVP submitted! ðŸŒ»");
      setForm({ ...form, full_name:"", email:"" }); setBehalfList([]);
    } catch(err){ toast.error(err.message); }
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <header className="text-center my-6">
        <h1 className="text-3xl font-extrabold text-soil-700">RSVP for <span className="text-sunflower-700">Abraham & Jesse-Lee</span></h1>
        <p className="text-soil-500">We canâ€™t wait to celebrate with you.</p>
      </header>
      <form onSubmit={submit} className="card p-6 space-y-4">
        <div className="grid md:grid-cols-2 gap-4">
          <input className="input" placeholder="Full Name" value={form.full_name} onChange={e=>setForm(f=>({...f, full_name:e.target.value}))} required />
          <input className="input" type="email" placeholder="Email" value={form.email} onChange={e=>setForm(f=>({...f, email:e.target.value}))} required />
        </div>
        <div className="grid md:grid-cols-2 gap-4">
          <select className="input" value={form.attending} onChange={e=>setForm(f=>({...f, attending: e.target.value === "true"}))}>
            <option value="true">Attending</option><option value="false">Not Attending</option>
          </select>
          <input className="input" placeholder="Dietary Requirements" value={form.dietary} onChange={e=>setForm(f=>({...f, dietary:e.target.value}))} />
        </div>
        <input className="input" placeholder="Song requests (comma-separated)" value={form.songs} onChange={e=>setForm(f=>({...f, songs:e.target.value}))} />
        <div className="card p-4">
          <label className="flex items-center gap-3">
            <input type="checkbox" checked={form.has_plus_one} onChange={e=>setForm(f=>({...f, has_plus_one:e.target.checked}))} />
            <span className="font-semibold text-soil-700">Bringing a plus one?</span>
          </label>
          {form.has_plus_one && (
            <div className="mt-3 grid md:grid-cols-2 gap-4">
              <input className="input" placeholder="Plus One Name" value={form.plus_one_name} onChange={e=>setForm(f=>({...f, plus_one_name:e.target.value}))} />
              <input className="input" placeholder="Plus One Dietary" value={form.plus_one_dietary} onChange={e=>setForm(f=>({...f, plus_one_dietary:e.target.value}))} />
            </div>
          )}
        </div>
        <div className="card p-4">
          <label className="flex items-center gap-3">
            <input type="checkbox" checked={form.has_children} onChange={e=>setForm(f=>({...f, has_children:e.target.checked}))} />
            <span className="font-semibold text-soil-700">Bringing children?</span>
          </label>
          {form.has_children && (
            <div className="mt-3 space-y-3">
              {form.children.map((c,i)=>(
                <div key={i} className="grid md:grid-cols-2 gap-3">
                  <input className="input" placeholder="Child Name" value={c.name} onChange={e=>{const copy=[...form.children]; copy[i].name=e.target.value; setForm(f=>({...f, children:copy}));}}/>
                  <input className="input" placeholder="Child Dietary" value={c.dietary} onChange={e=>{const copy=[...form.children]; copy[i].dietary=e.target.value; setForm(f=>({...f, children:copy}));}}/>
                </div>
              ))}
              <button type="button" className="btn-outline" onClick={()=>setForm(f=>({...f, children:[...f.children,{name:"", dietary:""}]}))}>+ Add Child</button>
            </div>
          )}
        </div>
        <div className="card p-4">
          <div className="flex items-center justify-between">
            <h3 className="font-bold text-soil-700">RSVP on behalf of others</h3>
            <button type="button" className="btn-outline" onClick={()=>setBehalfList(l=>[...l,{full_name:"", attending:true, dietary:"", songs:""}])}>+ Add Guest</button>
          </div>
          <div className="mt-3 space-y-3">
            {behalfList.map((g,idx)=>(
              <div key={idx} className="grid md:grid-cols-4 gap-3">
                <input className="input" placeholder="Full Name" value={g.full_name} onChange={e=>{const c=[...behalfList]; c[idx].full_name=e.target.value; setBehalfList(c);}} />
                <select className="input" value={g.attending} onChange={e=>{const c=[...behalfList]; c[idx].attending=(e.target.value==="true"); setBehalfList(c);}}>
                  <option value="true">Attending</option><option value="false">Not Attending</option>
                </select>
                <input className="input" placeholder="Dietary" value={g.dietary} onChange={e=>{const c=[...behalfList]; c[idx].dietary=e.target.value; setBehalfList(c);}} />
                <input className="input" placeholder="Songs" value={g.songs} onChange={e=>{const c=[...behalfList]; c[idx].songs=e.target.value; setBehalfList(c);}} />
              </div>
            ))}
          </div>
        </div>
        <div className="flex justify-end"><button className="btn-primary" type="submit">Submit RSVP</button></div>
      </form>
    </div>
  );
}
