import { useState } from "react";
import { useAuth } from "../context/AuthProvider";
import { toast } from "react-hot-toast";
import { Link } from "react-router-dom";

export default function SignupPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPw, setShowPw] = useState(false);
  const [loading, setLoading] = useState(false);
  const [formErr, setFormErr] = useState("");
  const { signUp } = useAuth();

  const validate = () => {
    if (!email) return "Email is required.";
    if (!/^\S+@\S+\.\S+$/.test(email)) return "Enter a valid email.";
    if (!password) return "Password is required.";
    if (password.length < 8) return "Password must be at least 8 characters.";
    return "";
  };

  const submit = async (e) => {
    e.preventDefault();
    setFormErr("");
    const v = validate();
    if (v) {
      setFormErr(v);
      return;
    }
    setLoading(true);
    try {
      await signUp({ email: email.trim(), password });
      toast.success("Check your email to confirm your account.");
    } catch (err) {
      const msg = err?.message || "Sign up failed. Please try again.";
      setFormErr(msg);
      toast.error(msg);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-[70vh] flex items-center justify-center p-4">
      <form onSubmit={submit} className="card p-6 w-full max-w-md">
        <h1 className="section-title text-center">Create Account ðŸŒ»</h1>

        {!!formErr && (
          <div className="mt-3 text-sm text-red-600 bg-red-50 border border-red-200 rounded-md p-2">
            {formErr}
          </div>
        )}

        <div className="mt-4 space-y-3">
          <label className="block">
            <span className="sr-only">Email</span>
            <input
              className="input"
              type="email"
              placeholder="Email"
              autoComplete="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              disabled={loading}
              required
            />
          </label>

          <label className="block relative">
            <span className="sr-only">Password</span>
            <input
              className="input pr-12"
              type={showPw ? "text" : "password"}
              placeholder="Password (min 8 chars)"
              autoComplete="new-password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              disabled={loading}
              required
              minLength={8}
            />
            <button
              type="button"
              className="absolute right-2 top-1/2 -translate-y-1/2 text-sm text-soil-600 hover:underline"
              onClick={() => setShowPw((s) => !s)}
              tabIndex={-1}
            >
              {showPw ? "Hide" : "Show"}
            </button>
          </label>

          <button className="btn-primary w-full" type="submit" disabled={loading}>
            {loading ? "Creatingâ€¦" : "Sign Up"}
          </button>

          <div className="text-center text-sm mt-2">
            <Link to="/login" className="text-soil-600 hover:underline">
              Have an account? Login
            </Link>
          </div>
        </div>
      </form>
    </div>
  );
}
